jQuery.fn.showSwitch = function(){
	if ($.browser.msie){
		$(this).each(function(){
			$(this).removeClass('contracted').addClass('expanded');
		});
	}else{
		hash = window.location.hash
		if(hash != ''){
			$(this)
				.filter(hash)
				.removeClass('contracted')
				.addClass('expanded')
				.find('.info');
		}else{
			$(this[0])
				.removeClass('contracted')
				.addClass('expanded')
				.find('.info');
		}
		
		$(this).click(function(){
			console.log('clicked');
			$(this)
				.siblings()
				.filter('.expanded')
				.removeClass('expanded')
				.addClass('contracted')
				.find('.info')
				.removeAttr('style');
			$(this)
				.removeClass('contracted')
				.addClass('expanded')
				.find('.info');
			$("html, body").animate(
				{ scrollTop: $(this).offset().top - 20 },
				500);
			
		});
	}
}

/*
INITIALISING CYCLE PLUGIN
*/

slidernav = '<nav class="slider-nav"><a class="prev">Previous</a><div class="pager"></div><a class="next">Next</a></nav><!--#slider-nav-->';

jQuery.fn.sliderInit = function(){
	$this = $(this[0]);
	var c = $this.children().length;
	p = $this.parent();
	if(c > 1){
		$this.after(slidernav);	
		$this.cycle({ 
			fx:     'fade', 
			speed:  'fast',
			timeout: 0, 
			pager:  $('.pager', p),
			next:   $('.next', p),
			prev:   $('.prev', p),
			containerResize: false,
			slideResize: false,
			fit: 1
		}).data('sliderinit' , true).sliderheight();
	}else{
		$this.data('sliderinit' , false);
	}
};

jQuery.fn.gallerySliderInit = function(){
	$this = $(this[0]);
	var c = $this.children().length;
	var p = $this.parent().parent();
	if(c > 1){
		//$(this).after(slidernav);
		$(slidernav).insertAfter('.slider-wrap');
		$(this).cycle({ 
			fx:     'fade', 
			speed:  'slow',
			timeout: 4000, 
			pager:  $('.pager', p),
			next:   $('.next', p),
			prev:   $('.prev', p),
			containerResize: false,
			slideResize: false,
			fit: 1
		}).data('sliderinit' , true).padSliderHeight();
	} else {
		$(this).data('sliderinit' , false);
	}
};

/*
MAKING SLIDES FLUID
*/

jQuery.fn.sliderheight = function() {
	if($(this).data('sliderinit') == true){
		$(this).each(function(){
			o = $(this);
			var maxHeight = 0;
			el = o.children();
			el.each(function(){
				//$(this).css('height' , '');
				if($(this).outerHeight(true) > maxHeight) {
					maxHeight = $(this).outerHeight(true);
				}
			});
			o.height(maxHeight);
		}).resize();
	}
};

jQuery.fn.releaseHeight = function() {
		o = $(this);
		var maxHeight = 0;
		o.each(function(){
			if($(this).outerHeight(true) > maxHeight) {
				maxHeight = $(this).outerHeight(true);
			}
		});
		o.height(maxHeight);
};

//latest

jQuery.fn.padSliderHeight = function() {
	if($(this).data('sliderinit') == true){
		$(this).imagesLoaded(function(){
			$this = $(this).children().first();
			ratio = $this.height() / $this.width();
			$(this)
				.css({'position' : 'absolute' , 'top' : '0', 'left' : '0', 'width' : '100%'})
			$(this)
				.parent('.slider-wrap')
				.css('padding-top', (ratio * 100) + "%");
		});
	}
};

$.fn.cycle.transitions.fadeOutWaitFadeIn = function($cont, $slides, opts) {
    opts.fxFn = function(curr, next, opts, after) {
    	cH = $(curr).height();
		cW = $(curr).width();
		cR = cH/cW;
		nH = $(next).height();
		nW = $(next).width();
		nR = nH/nW;
		if(cR == nR){
			$(curr).fadeOut(opts.speed);
			$(next).delay(opts.speed/2).fadeIn(opts.speed, function() {
					after();              
				});
		}else{
			$(curr).fadeOut(opts.speed, function() {
				$('.slider-wrap').css('padding-top', (nR * 100) + "%");
				$(next).delay(opts.delayBetweenFades).fadeIn(opts.speed, function() {
					after();              
				});
			});
		}
        
    };
};
